// Challenge / Exercise
import MainNavigation from "./components/MainNavigation";
import EventsPage, { loader as loaderAsyn } from "./pages/EventsPage";
import EventDetailPage, {
  loader as loaderAsynEvent,
} from "./pages/EventDetailPage";
import HomePage from "./pages/Homepage";
import {
  NavLink,
  RouterProvider,
  createBrowserRouter,
  useNavigation,
} from "react-router-dom";
import NewEventPage from "./pages/NewEventPage";
import Root from "./components/Root";
import EventRoot from "./pages/EventRoot";
import ErrorPage from "./pages/ErrorPage";

// 1. Add five new (dummy) page components (content can be simple <h1> elements)
//    - HomePage
//    - EventsPage
//    - EventDetailPage
//    - NewEventPage
//    - EditEventPage

// 2. Add routing & route definitions for these five pages
//    - / => HomePage
//    - /events => EventsPage
//    - /events/<some-id> => EventDetailPage
//    - /events/new => NewEventPage
//    - /events/<some-id>/edit => EditEventPage

// 3. Add a root layout that adds the <MainNavigation> component above all page components

// 4. Add properly working links to the MainNavigation

// 5. Ensure that the links in MainNavigation receive an "active" class when active

// 6. Output a list of dummy events to the EventsPage

// 7. Every list item should include a link to the respective EventDetailPage

// 7. Output the ID of the selected event on the EventDetailPage

// BONUS: Add another (nested) layout route that adds the <EventNavigation> component above all /events... page components

const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    errorElement: <ErrorPage />,
    children: [
      { index: true, element: <HomePage /> },
      {
        path: "events",
        element: <EventRoot />,
        children: [
          {
            index: true,
            element: <EventsPage />,
            loader: loaderAsyn,
          },
          {
            path: ":id1",
            element: <EventDetailPage />,
            loader: loaderAsynEvent,
          },
          { path: "new", element: <NewEventPage /> },
        ],
      },
    ],
  },
]);
function App() {
  return (
    <main>
      <h1>Hello from MAX Courses</h1>
      <RouterProvider router={router} />
    </main>
  );
}

export default App;
